// Change default file names as needed.

// Use each "page" component to assemble complete, standalone pages comprised of 
// various subcomponents. 

import React, { Component, useState, useEffect } from 'react'
import { NavLink } from 'react-router-dom'
import ApiClient from '../ApiClient'
import video from "../starwarsmontage.mp4"

let homeStyles = {

  navBar: {
    textAlign: "center", 
    backgroundColor: "black", 
    width: '100%', 
    minHeight: "80px",
    margin: "0",
    fontSize: "45px",
    paddingTop: "8px",
    paddingBotto: "8px",
  },

  title: {
    margin: "0",
    color: "yellow", 
  },

  shipGrid: {
    position: "relative",
    bottom: "65vh",
    "display": "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(150px, 1fr))",
    "gridGap": "1rem",
    "border": "4px solid white",
    width: "90%",
    margin: "auto"
  },

  shipSquares: {
    position: "relative",
    width: "150px",
    height: "150px",
    "backgroundColor": 'green',
    padding: "4px",
    overflow: "hidden",
    textAlign: "center"
  },

  Links: {
    color: "black",
    "textDecoration": "none",
    fontSize: "16px",
    display: "block",
    position: "absolute",
    left: "50%",
    top: "50%",
    transform: "translate(-50%, -50%)",
    margin: "0 auto"
  },

  videoWrapper: {
    width: "120%",
    // "zIndex": "-100",
    height: "75vh",
    position: "-webkit-sticky",
    position: 'sticky',
    top: "70px", 
    // transform: "translate(0, -50%)",
  },
  video: {
    overflow: "hidden"
  }
}

const Home = (props) => {

  const [starships, updateStarships] = useState([])
  const [fetchingStarships, findStarShips] = useState()


  useEffect( async () => {
    console.log("useEffect reached")
    const responsePage1 = await ApiClient.get("/")
    const responsePage2 = await ApiClient.get("/?page=2")
    const responsePage3 = await ApiClient.get("/?page=3")
    const responsePage4 = await ApiClient.get("/?page=4")

    const combinedShipArray = [...responsePage1.data.results, ...responsePage2.data.results,
      ...responsePage3.data.results, ...responsePage4.data.results
    ]
    console.log(combinedShipArray)
    updateStarships(combinedShipArray)
  }, [fetchingStarships]) 

  console.log("The starships in state: ", starships)
  return (
    <div className="homepageWrapper">
      <div className="navBar" style={homeStyles.navBar}>
        <h1 style={homeStyles.title}>Star Wars</h1>
      </div>

      <div style={homeStyles.videoWrapper}className="videoContainer">
        <video style={homeStyles.video} autoPlay loop muted src={video} type="video/mp4"> Your browser does not support the video tag.</video>
      </div>

      <div style={homeStyles.shipGrid}> 
        {starships.map((starship, index) => {
          // let location = {
          //   pathname: '/starship',
          //   state: starship
          // }
          return (

            <div key={`index${index}`} style={homeStyles.shipSquares}>
              {/* <NavLink to={location} key={starship.name}>{starship.name}</NavLink> */}
              <h3 className="shipLink" style={homeStyles.Links}>{starship.name}</h3>
            </div>
          )
        })}
      </div>
    </div>
  )
}


  export default Home
