import React, { Component, useState, useEffect } from 'react'
import { NavLink } from 'react-router-dom'
import ApiClient from '../ApiClient'

let homeStyles = {

  shipGrid: {
    "display": "grid",
    "grid-template-columns": "repeat(auto-fill, minmax(350px, 1fr))",
    "gridGap": "1rem",
    "border": "4px solid white",
  },

  shipSquares: {
    width: "300px",
    height: "300px",
    "backgroundColor": 'green',
    padding: "4px",
    "backgroundColor": "rgb(255, 174, 0)",
    overflow: "hidden"
  },

  Links: {
    color: "white",
    "fontSize": "14px",
    "textDecoration": "none"
  }
}

const Home = (props) => {

  const [starships, updateStarships] = useState([])
  const [fetchingStarships, findStarShips] = useState()


  useEffect(() => {
    console.log("useEffect reached")
    async function getStarships() {
      const shipResponse = await ApiClient.get("")
      console.log(shipResponse.data.results)
      updateStarships(shipResponse.data.results)
    }
    getStarships()


  }, [fetchingStarships]) 

  console.log("The starships in state: " , starships)
  return (

    <div className="shipGrid"> 

      {starships.map((starship, index) => {
        // let location = {
        //   pathname: '/starship',
        //   state: starship
        // }
        return (
          <div key={`index${index}`} style={homeStyles.shipSquares}>
            {/* <NavLink to={location} key={starship.name}>{starship.name}</NavLink> */}
            <h3 className="shipLink" style={homeStyles.Links}>{starship.name}</h3>
          </div>
        )
      })}

    </div>
  )
}


  export default Home
