import React, { Component, useState, useEffect } from 'react'
import "../styles/STARWARS/starwarsfont.css"
import imageUrls from '../imageUrls';





let starshipStyles = {
  appContentWrapper: {
    height: "100%",
    overflowX: "hidden",
    overflowY: "hidden",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat"

  },
  shipName: {
    alignText: "center",
  },
  picture: {

  }


}


function StarshipPage (props) {
  const backgroundImages = [
    "https://cdn.wallpapersafari.com/71/62/rKOEyo.jpg",
    "https://c4.wallpaperflare.com/wallpaper/1005/822/563/star-wars-death-star-at-at-space-wallpaper-preview.jpg",
    "https://starwarsblog.starwars.com/wp-content/uploads/2020/04/star-wars-backgrounds-20-1.jpg",
    "https://1.bp.blogspot.com/-GqCQoxHfW1Y/T5FdSTKtF8I/AAAAAAAAUK0/RAx4PQzGN94/s1600/space+wallpaper+(20).jpg",
    "https://wallpaperaccess.com/full/2137907.jpg"
]
  const randomIndex= Math.floor(Math.random() * backgroundImages.length)


  const {name, model, manufacturer, cost_in_credits, length, crew, passengers, cargo_capacity, starship_class} = props.starship
  const styleForWrapper = {...starshipStyles.appContentWrapper, backgroundImage: `url(${backgroundImages[randomIndex]})`}
  return (
    <main className="appContentWrapper" style={styleForWrapper}>
      <div style={starshipStyles.picture}></div>
      <h1 className="shipName">name</h1>
    </main>
  )
}

export default StarshipPage;
