import React, { Component, useState, useEffect } from 'react'
import "../styles/STARWARS/starwarsfont.css"
import imageUrls from '../imageUrls';





let starshipStyles = {
  appContentWrapper: {
    height: "100%",
    overflowX: "hidden",
    overflowY: "hidden",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat"

  },
  shipName: {
    alignText: "center",
  },
  picture: {

  }


}


function StarshipPage (props) {
  console.log(props)
  const backgroundImages = [

    "https://1.bp.blogspot.com/-GqCQoxHfW1Y/T5FdSTKtF8I/AAAAAAAAUK0/RAx4PQzGN94/s1600/space+wallpaper+(20).jpg",
    "https://wallpaperaccess.com/full/2137907.jpg"
]
  const randomIndex= Math.floor(Math.random() * backgroundImages.length)

  let starshipStyles = {
    appContentWrapper: {
      margin: "0 auto",
      width: "100vw",
      minHeight: "100vh",
      overflowX: "hidden",
      overflowY: "hidden",
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat"

    },
    picture: {
      margin: "0 auto",
      minHeight: "400px", 
      minWidth: "400px",
      maxHeight: "600px", 
      maxWidth: "600px",
      marginTop: "5vh",
      boxShadow: "0px 0px 15px white",
      backgroundImage: `${imageUrls[props.history.location.state.imageNum]}`,
      backgroundSize: "cover", 
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      border: "1px white solid"
    }, 

    detailWrapper: {
      margin: "0 auto",
      width: "70vw",
      backgroundColor: "rgba(0,0,0,.8)",
      padding: "13px"
    },
    shipName: {
      margin: "0 auto",
      textAlign: "center",
      display: "block", 
      marginTop: "50px",
      marginBottom: "30px",
      color: "yellow",
      fontSize: "40px", 
      fontFamily: "STARWARS"
    },

    shipDetail: {
      color: "white",
      fontSize: "20px",
      alignText: "left"
    }
  }

  const {name, manufacturer, cost_in_credits, length, crew, passengers, starship_class} = props.history.location.state.starship
  const styleForWrapper = {...starshipStyles.appContentWrapper, backgroundImage: `url(${backgroundImages[randomIndex]})`}
  return (
    <main className="appContentWrapper" style={styleForWrapper}>

      <div style={starshipStyles.picture}></div>
      <h1 className="shipName" style={starshipStyles.shipName}>{name}</h1>
      <div style={starshipStyles.detailWrapper}>
        <h3 style={starshipStyles.shipDetail}>Starship Class: {starship_class}</h3>
        <h3 style={starshipStyles.shipDetail}>Production Cost: {cost_in_credits}</h3>
        <h3 style={starshipStyles.shipDetail}>Crew Size: {crew}</h3>
        <h3 style={starshipStyles.shipDetail}>Passenger Capacity: {passengers}</h3>
        <h3 style={starshipStyles.shipDetail}>Manufacturer: {manufacturer}</h3>
      </div>


    </main>
  )
}

export default StarshipPage;
